import React from "react";
import styles from "./Login.module.scss";
import CustomInput from "../../components/UI/Input/CustomInput";
import { useForm } from "react-hook-form";
const Login = () => {
  const methods = useForm();
  const {
    controls,
    register,
    handleSubmit,
    setError,
    formState: { errors, isValid },
  } = methods;

  const onSave = (values) => {
    console.log(values);
  };

  return (
    <div className={styles.login}>
      <h2>Sign In</h2>
      <form onSubmit={handleSubmit(onSave)} className={styles.form}>
        <label>Email</label>
        <CustomInput
          className={Boolean(errors.email?.message) ? styles.wrong : ""}
          type="email"
          placeholder="email"
          {...register("email")}
        />
        <label>Password</label>
        <CustomInput
          className={Boolean(errors.email?.message) ? styles.wrong : ""}
          type="password"
          placeholder="password"
        />
        <button onClick={handleSubmit(onSave)} className="button-reverse">
          Sign In
        </button>
      </form>
    </div>
  );
};

export default Login;
