import React from "react";
import styles from "./Login.module.scss";
import CustomInput from "../../components/UI/Input/CustomInput";
import { Controller, FormProvider, useForm } from "react-hook-form";
import { useDispatch, useSelector } from "react-redux";
import { fetchUser, selectIsAuth } from "../../Redux/slices/sliceAuth";
const Login = () => {
  const dispatch = useDispatch();
  const isAuth = useSelector(selectIsAuth);
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm();

  const onSave = (values) => {
    console.log(values);
    dispatch(fetchUser(values));
  };
  const onError = (values) => {
    console.log(values);
  };
  return (
    <div className={styles.login}>
      <h2>Sign In</h2>
      <form onSubmit={handleSubmit(onSave, onError)} className={styles.form}>
        <label>Email</label>
        <CustomInput
          placeholder="email"
          type="text"
          helperText="Invalid email"
          isValid={Boolean(
            errors.password && errors.password.type === "minLength"
          )}
          {...register("email", { required: true })}
        />

        <label>Password</label>

        <CustomInput
          placeholder="password"
          type="password"
          helperText="Invalid password"
          isValid={Boolean(errors.password)}
          {...register("password", { required: true })}
        />

        <button className="button-reverse">Sign In</button>
      </form>
    </div>
  );
};

export default Login;
