import React from "react";
import FilterPosts from "../components/FilterPosts/FilterPosts";
import Post from "../components/Post/Post";
import SideBar from "../components/SideBar/SideBar";
import { useDispatch, useSelector } from "react-redux";
import { fetchPosts, fetchTags } from "../Redux/slices/slicePosts";
import PostSkeleton from "../components/Post/PostSkeleton";
const Home = () => {
  const dispatch = useDispatch();
  const { posts, tags, comments } = useSelector((state) => state.posts);
  const isLoadingPost = posts.status === "loading";
  const isLoadingTags = tags.status === "loading";

  React.useEffect(() => {
    dispatch(fetchPosts());
    dispatch(fetchTags());
  }, []);

  return (
    <section className="home">
      <FilterPosts />
      <div className="home__content">
        <div className="home__posts">
          {(isLoadingPost ? [...Array(3)] : posts.items).map((obj, index) =>
            isLoadingPost ? (
              <PostSkeleton key={index} />
            ) : (
              <Post key={index} item={obj} />
            )
          )}
        </div>

        <SideBar />
      </div>
    </section>
  );
};

export default Home;
