import React from "react";
import styles from "./Login.module.scss";
import CustomInput from "../../components/UI/Input/CustomInput";
import { Controller, FormProvider, useForm } from "react-hook-form";
import { useDispatch, useSelector } from "react-redux";
import { fetchUser, selectIsAuth } from "../../Redux/slices/sliceAuth";
import { Formik, useFormik } from "formik";
const Login = () => {
  const dispatch = useDispatch();

  const isAuth = useSelector(selectIsAuth);

  return (
    <div className={styles.login}>
      <h2>Sign In</h2>
      <Formik
        initialValues={{ email: "", passwordHash: "" }}
        validate={(values) => {
          const errors = {};
          if (!values.email) {
            errors.email = "Email is required";
          }
          if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
            errors.email = "Invalid email address";
          }
          if (!values.passwordHash) {
            errors.passwordHash = "Password is required";
          }
          if (values.passwordHash.length < 5) {
            errors.passwordHash =
              "The password must be at least 5 characters long.";
          }

          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          console.log(values);
          setSubmitting(false);
        }}
      >
        {({ values, errors, handleChange, handleSubmit, isSubmitting }) => (
          <form onSubmit={handleSubmit} className={styles.form}>
            <label>Email</label>
            <CustomInput
              placeholder="email"
              type="text"
              name="email"
              helperText="Invalid email"
              onChange={handleChange}
              value={values.email}
              error={Boolean(errors.email)}
            />

            <label>Password</label>

            <CustomInput
              placeholder="password"
              type="password"
              name="passwordHash"
              helperText="Invalid password"
              onChange={handleChange}
              value={values.passwordHash}
              error={Boolean(errors.passwordHash)}
            />

            <button
              type="submit"
              disabled={isSubmitting}
              className="button-reverse"
            >
              Sign In
            </button>
          </form>
        )}
      </Formik>
    </div>
  );
};

export default Login;
