import React from "react";
import styles from "./Registration.module.scss";
import CustomInput from "../../components/UI/Input/CustomInput";
import { Formik } from "formik";
const Registration = () => {
  return (
    <div className={styles.registration}>
      <h2>Sign Up</h2>
      <Formik
        initialValues={{
          email: "skaulin62@gmail.com",
          passwordHash: "1234567",
        }}
        validate={(values) => {
          const errors = {};
          if (!values.email) {
            errors.email = "Email is required";
          } else {
            if (
              !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
            ) {
              errors.email = "Invalid email address";
            }
          }

          if (!values.passwordHash) {
            errors.passwordHash = "Password is required";
          } else {
            if (values.passwordHash.length < 5) {
              errors.passwordHash = "The password must be at least 5.";
            }
          }

          return errors;
        }}
        onSubmit={async (values, { setSubmitting }) => {}}
      >
        {({ values, errors, handleChange, handleSubmit }) => (
          <form onSubmit={handleSubmit} className={styles.form}>
            <div className={styles.avatar}>
              <img src="assets/images/avatar-icon.webp" alt="avatar" />
            </div>
            <label>Email</label>
            <CustomInput
              className="wrong"
              type="email"
              name="email"
              value={values.email}
              onChange={handleChange}
              error={Boolean(errors.email)}
              placeholder="email"
            />
            <label>Name</label>
            <CustomInput className="wrong" type="text" placeholder="email" />
            <label>Last Name</label>
            <CustomInput className="wrong" type="text" placeholder="email" />

            <label>Password</label>
            <CustomInput type="password" placeholder="password" />
            <button className="button">Sign Up</button>
          </form>
        )}
      </Formik>
    </div>
  );
};

export default Registration;
