import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  posts: {
    items: [],
    status: "loading",
  },
  tags: {
    items: [],
    status: "loading",
  },
  comments: {
    items: [],
    status: "loading",
  },
};

const slicePosts = createSlice({
  name: "posts",
  initialState,
  reducers: {},
});

export const postsReducer = slicePosts.reducer;
