import React from "react";
import styles from "./Login.module.scss";
import CustomInput from "../../components/UI/Input/CustomInput";
import { Controller, FormProvider, useForm } from "react-hook-form";
import { useDispatch, useSelector } from "react-redux";
import { fetchUser, selectIsAuth } from "../../Redux/slices/sliceAuth";
const Login = () => {
  const dispatch = useDispatch();
  const isAuth = useSelector(selectIsAuth);
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const onSave = (values) => {
    console.log(values);
  };
  const onError = (values) => {
    console.log(values);
    dispatch(fetchUser(values));
  };

  return (
    <div className={styles.login}>
      <h2>Sign In</h2>
      <form onSubmit={handleSubmit(onSave, onError)} className={styles.form}>
        <label>Email</label>
        <CustomInput
          placeholder="email"
          type="text"
          {...register("email", {
            required: true,
          })}
        />
        {errors.email && <p>Error</p>}

        <label>Password</label>

        <CustomInput
          placeholder="password"
          type="password"
          {...register("password", {
            required: true,
            pattern: /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,15}$/,
          })}
        />
        {errors.password && <p>Error</p>}
        <button className="button-reverse">Sign In</button>
      </form>
    </div>
  );
};

export default Login;
