import React from "react";
import styles from "./Login.module.scss";
import CustomInput from "../../components/UI/Input/CustomInput";
import { Controller, FormProvider, useForm } from "react-hook-form";
import { useDispatch, useSelector } from "react-redux";
import { fetchUser, selectIsAuth } from "../../Redux/slices/sliceAuth";
const Login = () => {
  const dispatch = useDispatch();
  const isAuth = useSelector(selectIsAuth);
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm();

  const onSave = (values) => {
    console.log(values);
    dispatch(fetchUser(values));
  };
  const onError = (values) => {
    console.log(values.email?.message);
  };
  return (
    <div className={styles.login}>
      <h2>Sign In</h2>
      <form onSubmit={handleSubmit(onSave, onError)} className={styles.form}>
        <label>Email</label>
        <CustomInput
          placeholder="email"
          type="text"
          helperText="Invalid email"
          {...register("email", {
            required: true,
            pattern: /^[^@ ]+@[^@ ]+\.[^@ .]{2,}$/,
          })}
          isValid={Boolean(errors.email)}
        />

        <label>Password</label>

        <CustomInput
          placeholder="password"
          type="password"
          helperText="Invalid password"
          isValid={Boolean(
            errors.passwordHash && errors.passwordHash.type === "minLength"
          )}
          ref={register("passwordHash", { required: true, minLength: 5 })}
        />

        <button className="button-reverse">Sign In</button>
      </form>
    </div>
  );
};

export default Login;
