import React from "react";
import styles from "./AddPostPage.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { selectIsAuth } from "../../Redux/slices/sliceAuth";
import { Navigate } from "react-router";
import axios from "../../API/axios";
const AddPostPage = () => {
  const isAuth = useSelector(selectIsAuth);
  const dispatch = useDispatch();

  const [fields, setFields] = React.useState({
    title: "",
    tags: "",
    text: "",
    imageUrl: "",
  });
  const fileRef = React.useRef(null);

  if (!isAuth && !window.localStorage.getItem("token")) {
    return <Navigate to="/signin" />;
  }

  const handleChangeInput = async (event) => {
    try {
      const formData = new FormData();
      formData.append("image", event.target.files[0]);
      const { data } = await axios.post("/upload", formData);
      setFields({ ...fields, imageUrl: data.url });
    } catch (err) {
      console.log(err);
    }
  };

  console.log(fields);
  return (
    <div style={{ width: "100%", background: "white", borderRadius: "3px" }}>
      <div className={styles.createdPost}>
        <button onClick={() => fileRef.current.click()} className="button">
          Download Picture
        </button>

        {fields.imageUrl && (
          <div>
            <img
              width={250}
              height={250}
              src={`http://localhost:4000${fields.imageUrl}`}
            />
            <button
              className={styles.remove}
              style={{ border: "none" }}
              onClick={() => setFields({ ...fields, imageUrl: "" })}
            >
              Delete
            </button>
          </div>
        )}
        <input ref={fileRef} type="file" onChange={handleChangeInput} hidden />
        <input
          className={styles.createdPost__title}
          value={fields.title}
          onChange={(e) =>
            setFields({
              ...fields,
              title: e.target.value,
            })
          }
          placeholder="Article title..."
        />
        <input
          className={styles.createdPost__tags}
          placeholder="tags"
          value={fields.tags}
          onChange={(e) => {
            setFields({
              ...fields,
              tags: e.target.value,
            });
          }}
        />
        <textarea
          onChange={(e) =>
            setFields({
              ...fields,
              text: e.target.value,
            })
          }
          value={fields.text}
        />
        <button className="button-reverse">Create</button>
      </div>
    </div>
  );
};

export default AddPostPage;
