import React from "react";
import Comment from "../Comment/Comment";
import CustomLoader from "../Loader/CustomLoader";

const SideBar = ({ isLoading, tags }) => {
  const renderTags = () => {
    if (tags.length) {
      return (
        <>
          <h2>Tags</h2>
          <ul className="list-tags">
            {isLoading ? (
              <CustomLoader />
            ) : (
              tags.map((obj, index) => (
                <li key={index}>
                  <img
                    width={20}
                    height={20}
                    src="assets/images/tag.svg"
                    alt="tag"
                  />
                  <span className="tag">{obj}</span>
                </li>
              ))
            )}
          </ul>
        </>
      );
    } else {
      return <h2 style={{ margin: "10px auto" }}>Not tagsðŸ˜…</h2>;
    }
  };

  return (
    <div className="sidebar">
      <div className="sidebar__tags">{renderTags()}</div>
      <div className="sidebar__comments">
        <h2>Comments</h2>
        <div className="list-comments">
          <Comment />
          <Comment />
        </div>
      </div>
    </div>
  );
};

export default SideBar;
