import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "../../API/axios";

const initialState = {
  data: null,
  status: "loading",
};

const sliceAuth = createSlice({
  name: "auth",
  initialState,
  reducers: {},
  extraReducers: {},
});

export const authReducer = sliceAuth.actions;

export default sliceAuth.reducer;
