import React from "react";
import styles from "./Login.module.scss";
import CustomInput from "../../components/UI/Input/CustomInput";
import { Controller, FormProvider, useForm } from "react-hook-form";
const Login = () => {
  const methods = useForm();
  const {
    control,
    handleSubmit,
    formState: { errors, isValid },
    mode: onChange,
  } = methods;

  const onSave = (values) => {
    console.log(values);
  };

  return (
    <div className={styles.login}>
      <h2>Sign In</h2>
      <form onSubmit={handleSubmit(onSave)} className={styles.form}>
        <FormProvider {...methods}>
          <label>Email</label>
          <Controller
            name="email"
            type="email"
            control={control}
            render={({ field: { value, onChange } }) => (
              <CustomInput
                className={Boolean(errors.email?.message) ? styles.wrong : ""}
                placeholder="email"
                value={value}
                onChange={onChange}
              />
            )}
          />

          <label>Password</label>
          <Controller
            name="password"
            control={control}
            render={({ field: { value, onChange } }) => (
              <CustomInput
                className={Boolean(errors.email?.message) ? styles.wrong : ""}
                type="password"
                value={value}
                onChange={onChange}
                placeholder="password"
              />
            )}
          />

          <button onClick={handleSubmit(onSave)} className="button-reverse">
            Sign In
          </button>
        </FormProvider>
      </form>
    </div>
  );
};

export default Login;
