import React from "react";
import styles from "./Registration.module.scss";
import CustomInput from "../../components/UI/Input/CustomInput";
import { Formik } from "formik";
const Registration = () => {
  return (
    <div className={styles.registration}>
      <h2>Sign Up</h2>
      <Formik
        initialValues={{
          email: "",
          passwordHash: "",
          name: "",
          lastName: "",
        }}
        validate={(values) => {
          const errors = {};
          if (!values.email) {
            errors.email = "Email is required";
          } else {
            if (
              !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
            ) {
              errors.email = "Invalid email address";
            }
          }

          if (!values.passwordHash) {
            errors.passwordHash = "Password is required";
          } else {
            if (values.passwordHash.length < 5) {
              errors.passwordHash = "The password must be at least 5.";
            }
          }

          return errors;
        }}
        onSubmit={async (values, { setSubmitting }) => {
          console.log(values);
        }}
      >
        {({ values, errors, handleChange, handleSubmit }) => (
          <form onSubmit={handleSubmit} className={styles.form}>
            <div className={styles.avatar}>
              <img src="assets/images/avatar-icon.webp" alt="avatar" />
            </div>
            <label>Email</label>
            <CustomInput
              type="text"
              name="email"
              value={values.email}
              onChange={handleChange}
              error={Boolean(errors.email)}
              placeholder="email"
            />
            <label>Name</label>
            <CustomInput
              type="text"
              placeholder="name"
              name="name"
              value={values.name}
              onChange={handleChange}
              error={Boolean(errors.name)}
            />
            <label>Last Name</label>
            <CustomInput
              type="text"
              placeholder="lastname"
              name="lastName"
              value={values.lastName}
              onChange={handleChange}
              error={Boolean(errors.lastName)}
            />

            <label>Password</label>
            <CustomInput
              type="password"
              placeholder="password"
              name="passwordHash"
              value={values.passwordHash}
              onChange={handleChange}
              error={Boolean(errors.passwordHash)}
            />
            <button type="submit" className="button">
              Sign Up
            </button>
          </form>
        )}
      </Formik>
    </div>
  );
};

export default Registration;
