import React from "react";
import styles from "./CustomInput.module.scss";
import clsx from "clsx";

const CustomInput = (
  { isValid = true, className, helperText, ...props },
  ref = React.useRef()
) => {
  console.log(isValid);
  return (
    <div>
      <input ref className={`${styles.input} ` + className} {...props} />

      {isValid ? (
        <label className={clsx({ [styles.helperText]: true })}>
          {helperText}
        </label>
      ) : (
        ""
      )}
    </div>
  );
};

export default CustomInput;
