import React from "react";
import styles from "./Login.module.scss";
import CustomInput from "../../components/UI/Input/CustomInput";
import { Controller, FormProvider, useForm } from "react-hook-form";
const Login = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
    mode: onChange,
  } = useForm();

  const onSave = (values) => {
    console.log(values);
  };

  return (
    <div className={styles.login}>
      <h2>Sign In</h2>
      <form onSubmit={handleSubmit(onSave)} className={styles.form}>
        <label>Email</label>

        <CustomInput placeholder="email" type="email" {...register("email")} />

        <label>Password</label>

        <CustomInput
          placeholder="password"
          type="password"
          {...register("password", { required: true })}
        />
        {errors.password && <p>Please check the password Name</p>}

        <button onClick={handleSubmit(onSave)} className="button-reverse">
          Sign In
        </button>
      </form>
    </div>
  );
};

export default Login;
