import React from "react";
import styles from "./Login.module.scss";
import CustomInput from "../../components/UI/Input/CustomInput";
import { Controller, FormProvider, useForm } from "react-hook-form";
const Login = () => {
  const methods = useForm();
  const {
    control,
    register,
    handleSubmit,
    setError,
    formState: { errors, isValid },
  } = methods;

  const onSave = (values) => {
    console.log(values);
  };

  return (
    <div className={styles.login}>
      <h2>Sign In</h2>
      <form onSubmit={handleSubmit(onSave)} className={styles.form}>
        <FormProvider {...methods}>
          <label>Email</label>
          <Controller
            name="email"
            control={control}
            render={({ field: value, onChange }) => {
              <CustomInput value={value} onChange={onChange} />;
            }}
          />
          <label>Password</label>
          <CustomInput
            className={Boolean(errors.email?.message) ? styles.wrong : ""}
            type="password"
            placeholder="password"
          />
          <button onClick={handleSubmit(onSave)} className="button-reverse">
            Sign In
          </button>
        </FormProvider>
      </form>
    </div>
  );
};

export default Login;
